script.script:
if (order == undefined || order == null)
      return (-1);

if (versionName == undefined || versionName == null)
      return (-1);

if (order.versions == null)
      order.addItem ("versions");

var orderVersion = order.versions.newInstance ();
orderVersion.header.versionName = versionName;

return (order.versions.count - 1);
