script.script:
var genericProcessMsgFinder = new Finder ("stcw:findGenericProcessReceivedMsgById");
genericProcessMsgFinder.searchDocument.docId = docId;
genericProcessMsgFinder.search ();

var msgList = genericProcessMsgFinder.list;
if (msgList == null || msgList.size <= 0)
      return (null);

return stcw.readStructure (msgList.start.receivedMsg, dataType);
