script.script:
var processIdFinder = new Finder ("stcw:processIdsForOrderProcessesRunning");
processIdFinder.search ();

var processIdList = processIdFinder.list;
if (processIdList == null || processIdList.size <= 0)
      return (null);

var processIds = null;
for (var i = 0; i < processIdList.size; i++) {
      processIds = (processIds == null ? "" : processIds + ";") + processIdList[i].PROCESS_ID;
}

return processIds;
