var rejectedOrderIdFinder = new Finder ("stcw:rejectedOrderIdFinder");
rejectedOrderIdFinder.search ();

var orderIdList = rejectedOrderIdFinder.list;
if (orderIdList == null || orderIdList.size <= 0)
      return (null);

var orderIds = null;
for (var i = 0; i < orderIdList.size; i++) {
      orderIds = (orderIds == null ? "" : orderIds + ";") + orderIdList[i].orderId;
}

