var interfacePosition = null;

if(crmOrder != null) {
    if(crmOrder.versions != null && crmOrder.versions.count>versionPosition) {
        var versionInOrder = crmOrder.versions[versionPosition];
        if(versionInOrder.interfaces != null) {
            for(var v=0; v<versionInOrder.interfaces.count && (interfacePosition == null); v++) {
                if(versionInOrder.interfaces[v].interfaceData.interfaceName == interfaceName) {
                    interfacePosition = v;
                }
            }
        }
    }
}

return interfacePosition;
