method.script:
// search tasks of the WO
this.taskList = granite.getAllTasksByWOInstIdForFOPages(this.model.WO_INST_ID);

// search ont details
var ontEquipmentDetails = granite.getONTEquipmentDetailsByPathInstId(this.model.CIRC_PATH_INST_ID);
if(ontEquipmentDetails != null) {
    this.ontEquipmentDetailsUI = new granite.ontEquipmentDetails.UserInterface(ontEquipmentDetails);
}
else {
    this.ontEquipmentDetailsUI = null;
}

// search olt details
var oltEquipmentDetails = granite.getOLTEquipmentDetailsByPathInstId(this.model.CIRC_PATH_INST_ID);
if(oltEquipmentDetails != null) {
    this.oltEquipmentDetailsUI = new granite.oltEquipmentDetails.UserInterface(oltEquipmentDetails);
}
else {
    this.oltEquipmentDetailsUI = null;
}

// search parent services of the path
this.resourcesList = granite.getAllResourcesLinkedToPathForFOPages(this.model.CIRC_PATH_INST_ID, 10);

this.woWebXngHyperLink = granite.getWebXngURLFor("WO", this.model.WO_INST_ID);
this.pathWebXngHyperLink = granite.getWebXngURLFor("PATH", this.model.CIRC_PATH_INST_ID);

var selectedRows = this.taskList.selected;

if(selectedRows == null) {
    Global.throwException("UU0115");
}
else if(selectedRows.length > 1) {
    Global.throwException("UU0116");
}
else {
    var system = failedOrders.getSystemForMsgForTaskOperation(selectedRows[0].TASK_OPERATION);
    if(system != null) {
        if(system == "RLL") {
            var finder = new Finder("failedOrders.findRequestsInRLL");
            finder.searchDocument.TASK_NUM = selectedRows[0].TASK_NUMBER;
            finder.searchDocument.WO_NAME = this.model.WO_NAME;

            finder.search();
            return finder;
        }
        else {
            Global.throwException("System not found for the selected operation: " + selectedRows[0].TASK_OPERATION);
        }
    }
    else {
        Global.throwException("Messages not allowed for the selected operation: " + selectedRows[0].TASK_OPERATION);
    }

}

var selectedRows = this.taskList.selected;

if(selectedRows == null) {
    Global.throwException("UU0115");
}

if(selectedRows.length > 1) {
    Global.throwException("UU0116");
}

// checking permission to perform the action
if(! failedOrders.checkIfActionIsPermittedOnTask(selectedRows[0], this.model.STATE_PROV, this.model.WO_ORDER_TYPE)) {
    Global.throwException("AE0215");
}

// perform action
var ontEquipmentDetails = (this.ontEquipmentDetailsUI != null ? this.ontEquipmentDetailsUI.model : null);
failedOrders.performReSendActionOnSelectedTask(selectedRows[0], this.model.WO_NAME, this.taskList, this.model.CIRC_PATH_INST_ID, ontEquipmentDetails, false);

// log user action
failedOrders.insertNewUserActionForFO(Global.getUserId(), "Resend");

this.refreshUI();

var selectedRows = this.taskList.selected;

if(selectedRows == null) {
    Global.throwException("UU0115");
}

if(selectedRows.length > 1) {
    Global.throwException("UU0116");
}

// checking permission to perform the action
if(! failedOrders.checkIfActionIsPermittedOnTask(selectedRows[0], this.model.STATE_PROV, this.model.WO_ORDER_TYPE)) {
    Global.throwException("AE0215");
}


// perform action
var ontEquipmentDetails = (this.ontEquipmentDetailsUI != null ? this.ontEquipmentDetailsUI.model : null);
var returnedObject = failedOrders.performCompleteActionOnSelectedTask(selectedRows[0], this.model.WO_NAME, this.taskList, this.model.CIRC_PATH_INST_ID, ontEquipmentDetails);

if(returnedObject != null) {
    return returnedObject;
}

// log user action
failedOrders.insertNewUserActionForFO(Global.getUserId(), "Complete");

this.refreshUI();

var selectedRows = this.taskList.selected;

if(selectedRows == null) {
    Global.throwException("UU0115");
}

if(selectedRows.length > 1) {
    Global.throwException("UU0116");
}

// checking permission to perform the action
if(! failedOrders.checkIfActionIsPermittedOnTask(selectedRows[0], this.model.STATE_PROV, this.model.WO_ORDER_TYPE)) {
    Global.throwException("AE0215");
}


// perform action
var ontEquipmentDetails = (this.ontEquipmentDetailsUI != null ? this.ontEquipmentDetailsUI.model : null);
var returnedObject = failedOrders.performReSendActionOnSelectedTask(selectedRows[0], this.model.WO_NAME, this.taskList, this.model.CIRC_PATH_INST_ID, ontEquipmentDetails, true);

if(returnedObject != null) {
    return returnedObject;
}

// log user action
failedOrders.insertNewUserActionForFO(Global.getUserId(), "Resend With Additional Information");

this.refreshUI();
