script.script:
var systemName = null;

if(receivedTaskOperation != null) {
    var searchDoc = new Document("failedOrders.STCW_TASKOP_SYSTEM");
    searchDoc.taskOperation = receivedTaskOperation;

    var resultList = Finder.runFinder("failedOrders.findSystemForTaskOperation", "select", searchDoc);

    if(resultList != null && resultList.size > 0) {
        systemName = resultList[0].system;
    }
}

return systemName;
