var resultDoc = null;

if(taskOperation != null && statusCode != null) {
    var searchDoc = new Document("failedOrders.search_TaskToReprocessBehavior");
    searchDoc.taskOperation = taskOperation;
    searchDoc.statusCode = statusCode;

    var resultList = Finder.runFinder("failedOrders.findTaskToReprocessBehavior", "select", searchDoc);

    if(resultList != null && resultList.size > 0) {
        resultDoc = resultList[0];
    }
}

return resultDoc;
