script.script:
var siteExists = false;

if(siteHumId != null) {
    var siteFinder = new Finder("granite.siteFinder");
    siteFinder.searchDocument.INST_ID = null;
    siteFinder.searchDocument.SITE_HUM_ID = siteHumId;

    var siteList = siteFinder.search();
    if(siteList != null && siteList.size > 0) {
        siteExists = true;
    }
}

return siteExists;
