script.script:
var amo = null;

if(resourceName != null) {
    var resourceFinder = new Finder("granite.resourceFinder");
    resourceFinder.searchDocument.NAME = resourceName;

    var resourceList = resourceFinder.search();
    if(resourceList != null && resourceList.size > 0) {
        amo = resourceList[0];
    }
}

return amo;
