script.script:
var result = null;

if (customerId != null) {

   var input = new Document("granite:search_VAL_CUSTOMER");
   input.CUSTOMER_ID = customerId;

   var output = Finder.runFinder("granite.findCustomerByIdAndNameAndType", "select", input);

   if (output != null && output.size > 0) {
      result = output[0];
   }
}

return result;
