script.script:
var result = null;

if (statusCode != null) {
    var input = new Document("granite.valTaskStatusSearchDoc");
    input.STAT_CODE = statusCode;

    var output = Finder.runFinder("granite.findValTaskStatusByCode", "select", input);

    if (output != null && output.size > 0) {
        result = output[0].STATUS_NAME;
    }
}
return result;
