script.script:
var definitionName = null;

if(definitionInstId != null) {
    var inputDoc = new Document("granite.graniteInstId_10_SearchDoc");
    inputDoc.INST_ID = definitionInstId;

    var resultList = Finder.runFinder("granite.resourceDefinitionFinder", "select", inputDoc);

    if(resultList != null && resultList.size > 0) {
        definitionName = resultList[0].RESOURCE_DEFINITION_NAME;
    }
}

return definitionName;
