if(pathInstId != null) {
    var searchDoc = new Document("granite.graniteInstId_10_SearchDoc");
    searchDoc.INST_ID = pathInstId;

    var resultList = Finder.runFinder("granite.findOLTEquipmentDetailsByPathInstId", "select", searchDoc);
