script.script:
var path = null;

if(pathInstId != null) {
    var pathFinder = new Finder("granite.pathFinder");
    pathFinder.searchDocument.INST_ID = pathInstId;

    var pathList = pathFinder.search();
    if(pathList != null && pathList.size > 0) {
        path = pathList[0];
    }
}

return path;
