script.script:
var path = null;

if(pathName != null) {
    var pathFinder = new Finder("granite.pathFinder");
    pathFinder.searchDocument.CIRC_PATH_HUM_ID = pathName;

    var pathList = pathFinder.search();
    if(pathList != null && pathList.size > 0) {
        path = pathList[0];
    }
}

return path;
