script.script:
var queueName = null;

if(queueInstId != null) {
    var queueFinder = new Finder("granite.queueFinder");
    queueFinder.searchDocument.INST_ID = queueInstId;

    var queueList = queueFinder.search();
    if(queueList != null && queueList.size > 0) {
        queueName = queueList[0].QUEUE_NAME;
    }
}

return queueName;
