script.script:
var task = null;

if(taskInstId != null) {
    var taskFinder = new Finder("granite.taskFinder");
    taskFinder.searchDocument.INST_ID = taskInstId;

    var taskList = taskFinder.search();
    if(taskList != null && taskList.size > 0) {
        task = taskList[0];
    }
}

return task;
