script.script:
var result = null;

if (resourceName != null && udaName != null) {

   var input = new Document("granite:udaSearchDoc");
   input.ELEMENT_NAME = resourceName;
   input.ATTR_NAME = udaName;
   input.GROUP_NAME = udaGroupName;

   var output = Finder.runFinder("granite:findUDAValueInGraniteByResourceName_UDAName", "select", input);

   if (output != null && output.size > 0) {
      result = output[0].ATTR_VALUE;
   }
}

return result;
