script.script:
var result = null;

if (woName != null) {
    var input = new Document("granite.workOrderInfoSearchDoc");
    input.WO_NAME = woName;

    var output = Finder.runFinder("granite:findWOInGraniteByWOName", "select", input);

    if (output != null && output.size > 0) {
        result = output[0];
    }
}
return result;
