script.script:
var woName = null;

if(resourceName != null) {
    var woNameFinder = new Finder("granite.getWONameByResourceName");
    woNameFinder.searchDocument.NAME = resourceName;

    var woNameList = woNameFinder.search();
    if(woNameList != null && woNameList.size > 0) {
        woName = woNameList[0].WO_NAME;
    }
}

if(woName == null) {
    var mrLaptop = Global.getConfigVariable("MR_LAPTOP", "N");
    if(mrLaptop != null && mrLaptop == "Y") {
        woName = 'XYZ_WONAME_TEST';
    }
}


return woName;
