script.script:
var hyperLink = Global.getConfigVariable("WebXng_URL");

if(type == "WO") {
    hyperLink =  hyperLink + "xweb_works.xperworks_show_wo?woinstid=" + instId;
}
else if(type == "PATH") {
    hyperLink = hyperLink + "xperweb.path_def?iPathInstId=" + instId;
}
else if(type == "EQUIP") {
    hyperLink = hyperLink + "xweb_equip.equip_def?iEqInstId=" + instId;
}
else {
    hyperLink = null;
}

return hyperLink;
