script.script:
var workOrder = null;

if(woInstId != null) {
    var workOrderFinder = new Finder("granite.workOrderFinder");
    workOrderFinder.searchDocument.INST_ID = woInstId;

    var workOrderList = workOrderFinder.search();
    if(workOrderList != null && workOrderList.size > 0) {
        workOrder = workOrderList[0];
    }
}

return workOrder;
