script.script:
var debugLog = "InvokeCompleteTaskAPI";
var resultDS =  null;

try {
    var inputDS = new DataStructure("granitews4eoc.completeTask_el");
    inputDS.CompleteTaskInputMessage.taskInstId = taskInstId;
    inputDS.CompleteTaskInputMessage.userId = userId;

    resultDS = stcc.sendDataToInterfaceWithLogging(inputDS,
                                                   "granitews4eoc.GraniteServiceForEOCPortType",
                                                   "completeTask",
                                                   true,
                                                   null,
                                                   debugLog,
                                                   woName,
                                                   taskName);

}
catch(exc) {
    var errorType = "Application";

    if(exc.message.indexOf("XE0023") >= 0 || exc.message.indexOf("XE0002")) {
debugPrintln(stcc.getSysdateForLog() + " " + debugLog + " (" + taskInstId + "): Interface Error while invoking Granite For EOC WS: " + exc.message);
        errorType = "Interface";
    }
    else {
debugPrintln(stcc.getSysdateForLog() + " " + debugLog + " (" + taskInstId + "): Application Error while invoking Granite For EOC WS: " + exc.message);
        errorType = "Application";
    }

    resultDS = new DataStructure("granitews4eoc.completeTaskResponse_el");
    resultDS.ResultMessage.result = "ERROR";
    resultDS.ResultMessage.errorCode = "WS0001";
    resultDS.ResultMessage.errorDescription = exc.message;
}

debugPrintln(stcc.getSysdateForLog() + " " + debugLog + " (" + taskInstId + "): Response = " + resultDS.toXML());


return resultDS;
