script.script:
var debugHeader = "[" + processId + "] " + operationDescription + " - ";
var result = null;

try {
debugPrintln(stcc.getSysdateForLog() + debugHeader + "Sending: " + request.toXML());

            var outputSOAP = Global.invokeInterface(interfaceName,
                                                                        operationName,
                                                                        request);

            if(outputSOAP != null && outputSOAP.length == 1) {
                  result = outputSOAP[0];

debugPrintln(stcc.getSysdateForLog() + debugHeader + "RESULT: " + result.toXML() + "...");
            }

      }
      catch (exc){
debugPrintln(debugHeader + "EXCEPTION: " + exc.message);

            if(exc.message.indexOf("XE0023") >= 0) {
                  Global.logDebug(debugHeader + "I/F " + interfaceName + ": getDelay = " + Global.getInterfaceDelay(interfaceName));
debugPrintln(stcc.getSysdateForLog() + debugHeader + "I/F " + interfaceName + ": getDelay = " + Global.getInterfaceDelay(interfaceName));
            }
      }

      return result;
