method.script:
var debugHeader = "[" + this.process.id + "] SendQuoteToGranite - ";

var order = this.process.processOrder;
var processDocument = this.process.processDocument;
processDocument.exceptionType = null;

try {
      // extracting data must be sent to Granite
      var crmInput = findCrmInput_ (order);
      var request = new DataStructure("grws:validateQuote");

debugPrintln(stcc.getSysdateForLog() + debugHeader + "crmInput = '" + crmInput.toXML() + "'");

            stcw.map_crmQuote_graniteQuote (request, crmInput, order.header.orderRowItemId, order.header.quoteNumber, order.header.reservationNumber);

            var activityName = "GR_VALIDATE_QUOTE";
            stcw.ensureInterfaceFolder (order, activityName);
            order.save ();

            // sending data to Granite
            var response = null;

            if(order.header.skipSendToGranite == null || order.header.skipSendToGranite == "N") {
                  // check if the quote must be repushed or not
                  if(stcw.checkIfSendQuoteToGraniteMustBeSkipped(order, this.process.id)) {
                        order.header.skipSendToGranite = "Y";
                  }
            }

            if(order.header.skipSendToGranite == null || order.header.skipSendToGranite == "N") {
                  // sending data to Granite
                  response = stcw.sendValidateQuoteToGranite(request, this.process.id);

                  // managing response
                  rpts.updateHistoryEvent (this);
            }
            else {
debugPrintln(stcc.getSysdateForLog() + debugHeader + "skipping because order.header.skipSendToGranite = '" + order.header.skipSendToGranite + "'");

                  // skipping send order to granite ... and generating fake response ...
                  var response = new DataStructure("grws:validateQuoteResponse");
                  response.result.BusinessUnit = "Wholesale";
                  response.result.QuoteNumber = order.header.quoteNumber;
                  response.result.Status.Status = "SUCCESS";
                  response.result.WorkOrderStatus = "New";
            }

            var interfaceFolder = stcw.storeInterfaceMessage (stcw.findLastInterfaceFolder (order, activityName), response);
            if (interfaceFolder != null)
                  this.process.processOrderItemId = interfaceFolder.interfaceData.id;

            if (response == null) {
                  processDocument.exceptionType = "Interface";
            }
            else {
                  processDocument.ackResult = response.result.Status.Status;
                  processDocument.errorText = stcc.limitStringLengthToCRMValue(response.result.Status.ErrorDescription);
                   processDocument.activityName = activityName;
            }


            order.save ();
      }
      catch(exc) {
debugPrintln(stcc.getSysdateForLog() + debugHeader + "Catched Exception " + exc.message);
            processDocument.exceptionType = "Application";
      }

      //
      //  findCrmInput_
      //
      function findCrmInput_ (order) {

            var crmInterfaceFolder = stcw.findLastInterfaceFolder (order, "CRM_QUOTE_VALIDATION");
            if (crmInterfaceFolder == null) {
                  Global.logDebug ("failed to get crmInterfaceFolder [CRM_QUOTE_VALIDATION]");
                  return (null);
            }

            return (stcw.readStructure (crmInterfaceFolder.interfaceData.responseData , "wsws:quoteValidation"));

      }            // f i n d C r m I n p u t _
