method.script:
var debugHeader = "[" + this.process.id + "] - SendOrderAck - ";

var order = this.process.processOrder;
var processDocument = this.process.processDocument;
processDocument.exceptionType = null;

try {
      var isUpdate = (order.versions.length > 1);

      // extracting Granite response must be forward to EAI
      var graniteResponse = findGraniteResponse_ (order, isUpdate);
      var request = new DataStructure("siordAck:processRequest_el");

debugPrintln(stcc.getSysdateForLog() + debugHeader + "graniteResponse = '" + graniteResponse.toXML() + "'");

            if(isUpdate) {
                  stcw.map_updateOrderResp_orderAck(request, graniteResponse);
            }
            else {
                  stcw.map_submitOrderResp_orderAck (request, graniteResponse);
            }

            stcw.ensureInterfaceFolder (order, "CRM_ORDER_ACK");
            order.save ();

            // check payload size
            stcc.checkPayloadToCRMSize(request, this.process.processDocument.interOperationName);

            // sending data to EAI
            var response = stcw.sendOrderAckToEAI(request, this.process.id);

            // managing response
            rpts.updateHistoryEvent (this);

            var interfaceFolder = stcw.storeInterfaceMessage (stcw.findLastInterfaceFolder (order, "CRM_ORDER_ACK"), response);
            if (interfaceFolder != null)
                  process.processOrderItemId = interfaceFolder.interfaceData.id;

            if (response == null) {
                  processDocument.exceptionType = "Interface";
            }

            order.save ();
      }
      catch(exc) {
debugPrintln(stcc.getSysdateForLog() + debugHeader + "Catched Exception " + exc.message);
            processDocument.exceptionType = "Application";
      }

      //
      //  findGraniteResponse_
      //
      function findGraniteResponse_ (order, isUpdate) {
            var folderName = "GR_SUB_ORDER";
            var dataType   = "grws:submitOrderResponse";

            if(isUpdate) {
                  // it's an update
                  folderName = "GR_UPD_ORDER";
                  dataType   = "grws:updateOrderResponse";
            }

debugPrintln(stcc.getSysdateForLog() + debugHeader + "Checking in folder " + folderName + " - dataType = " + dataType + " - order = " + order.toXML());

            var graniteInterfaceFolder = stcw.findLastInterfaceFolder (order, folderName);
            if (graniteInterfaceFolder == null) {
                  Global.logDebug ("failed to get graniteInterfaceFolder: " + folderName);
                  return (null);
            }

            return (stcw.readStructure (graniteInterfaceFolder.interfaceData.responseData , dataType));

      }            // f i n d G r a n i t e R e s p o n s e _
