method.script:
var debugHeader = "[" + this.process.id + "] - SendQuoteAck - ";

var order = this.process.processOrder;
var processDocument = this.process.processDocument;
processDocument.exceptionType = null;

try {
      // extracting Granite response must be forward to EAI
      var graniteResponse = findGraniteResponse_ (order, processDocument);
      var request = new DataStructure("siquoAck:processRequest_el");

debugPrintln(stcc.getSysdateForLog() + debugHeader + "graniteResponse = '" + graniteResponse.toXML() + "'");

            stcw.map_quoteValidationResp_quoteAck (request, graniteResponse, order.header.orderNumber, order.header.orderRowItemId);

            var activityName = "CRM_QUOTE_ACK";
            stcw.ensureInterfaceFolder (order, activityName);
            order.save ();

            // check payload size
            stcc.checkPayloadToCRMSize(request, this.process.processDocument.interOperationName);

            // sending data to EAI
            var response = stcw.sendQuoteAckToEAI(request, this.process.id);

            // managing response
            rpts.updateHistoryEvent (this);

            var interfaceFolder = stcw.storeInterfaceMessage (stcw.findLastInterfaceFolder (order, activityName), response);
            if (interfaceFolder != null)
                  process.processOrderItemId = interfaceFolder.interfaceData.id;

            if (response == null) {
                  processDocument.exceptionType = "Interface";
            }
            else {
                  processDocument.activityName = activityName;
            }

            order.save ();
      }
      catch(exc) {
debugPrintln(stcc.getSysdateForLog() + debugHeader + "Catched Exception " + exc.message);
            processDocument.exceptionType = "Application";
      }

      //
      //  findGraniteResponse_
      //
      function findGraniteResponse_ (order, processDocument) {

            var graniteInterfaceFolder = stcw.findLastInterfaceFolder (order, processDocument.activityName);
            if (graniteInterfaceFolder == null) {
                  Global.logDebug ("failed to get Folder " + processDocument.activityName + " in order " + order.id);
                  return (null);
            }

            return (stcw.readStructure (graniteInterfaceFolder.interfaceData.responseData , "grws:validateQuoteResponse"));

      }            // f i n d G r a n i t e R e s p o n s e _
