method.script:
var debugHeader = "[" + this.process.id + "]";

var resultList = Finder.runFinder("stcw.findInfoForCompletedWO", "select", null);

if(resultList != null && resultList.length > 0){
    for(var i=0; i < resultList.length; i++){
        var inforForCompletedWO = resultList[i];
        if(inforForCompletedWO.provisioningBU == "E"){
            debugPrintln(stcc.getSysdateForLog() + debugHeader + "- Generating WOSU for EBU # Work Order: "+inforForCompletedWO.workOrderNumber);
            var isForQuote = (inforForCompletedWO.orderType == "F");
            var WOSU = stcw.generateCompletionWOSUForEBU(inforForCompletedWO, isForQuote);
            if(WOSU == null){
                debugPrintln(stcc.getSysdateForLog() + debugHeader + "- Can not build WOSU for : "+inforForCompletedWO.workOrderNumber);
                continue;
            }
            debugPrintln(stcc.getSysdateForLog() + debugHeader + "- WOSU to send: "+WOSU.toXML());
            cimeJMS.manageGraniteMsg_WorkOrderStatusUpdate(WOSU);
        }
        else {
            //If linked to quote
            if(inforForCompletedWO.orderType == "F"){
                debugPrintln(stcc.getSysdateForLog() + debugHeader + "- Generating WOSU for Quote # Work Order: "+inforForCompletedWO.workOrderNumber);
                var WOSU = stcw.generateCompletionWOSUForQuoteWBU(inforForCompletedWO.processId, inforForCompletedWO.workOrderNumber, inforForCompletedWO.orderNumber, inforForCompletedWO.orderRowItemId, inforForCompletedWO.reservationNumber);
                if(WOSU == null){
                    debugPrintln(stcc.getSysdateForLog() + debugHeader + "- Can not build WOSU for : "+inforForCompletedWO.workOrderNumber);
                    continue;
                }
                debugPrintln(stcc.getSysdateForLog() + debugHeader + "- WOSU to send: "+WOSU.toXML());
                var lineItem = Document.readDoc("stcw.lineItemDocument", inforForCompletedWO.cwDocId);
                wgws.processReceivedQSU(WOSU, inforForCompletedWO.orderNumber, lineItem);
            }
            else {
                debugPrintln(stcc.getSysdateForLog() + debugHeader + "- Generating WOSU for Order # Work Order: "+inforForCompletedWO.workOrderNumber);
                var WOSU = stcw.generateCompletionWOSUForOrderWBU(inforForCompletedWO.processId, inforForCompletedWO.workOrderNumber, inforForCompletedWO.orderType);
                if(WOSU == null){
                    debugPrintln(stcc.getSysdateForLog() + debugHeader + "- Can not build WOSU for : "+inforForCompletedWO.workOrderNumber);
                    continue;
                }
                debugPrintln(stcc.getSysdateForLog() + debugHeader + "- WOSU to send: "+WOSU.toXML());
                wgws.processReceivedOSU(WOSU, inforForCompletedWO.orderNumber, inforForCompletedWO.workOrderNumber);
            }
        }
    }
}
else {
    debugPrintln(stcc.getSysdateForLog() + debugHeader + "- No Work Orders found for Completion Sync");
}
