method.script:
var sleepTime = Global.getConfigVariable("GLOBAL_COMPLETION_WOSU_SLEEP_TIME_MINUTES", 30);
var sleepTimeNumber = new Number(sleepTime);
if(sleepTimeNumber != Number.NAN) {
   sleepTimeNumber = sleepTimeNumber*60;
}
else {
   sleepTimeNumber = 30*60;
}

debugPrintln(stcc.getSysdateForLog() + "[" + this.id + "] - Generate Completion WOSU for Completed WO - SleepTime = " + sleepTimeNumber);
return sleepTimeNumber;

var configExecuteGlobalCompletionWOSU = Global.getConfigVariable("EXECUTE_GLOBAL_COMPLETION_WOSU", "FALSE").toUpperCase();

var debugHeader = "[" + this.process.id + "] - EXECUTE_GLOBAL_COMPLETION_WOSU = ";
debugPrintln(stcc.getSysdateForLog() + debugHeader + configExecuteGlobalCompletionWOSU);

return (configExecuteGlobalCompletionWOSU == "TRUE");
