method.script:
var debugHeader = "[" + this.process.id + "] - ReceiveGraniteUpdate - ";

rpts.updateHistoryEvent (this, "graniteStatusUpdate");

var inputMessage = this.activityData;
var order = this.process.processOrder;

debugPrintln(stcc.getSysdateForLog() + debugHeader + "received " + (inputMessage != null ? inputMessage.toXML() : "NULL") + " to process " + process.id);

      stcw.storeInterfaceMessage (stcw.createInterfaceFolder (order, "GR_STATUS_UPD"), inputMessage);

      // Moved in "Change Order Status" - "Before" to solve the issue from Raj
      // received 07 July 2011
      // order.header.orderStatus = inputMessage.workOrderStatus;
      order.save ();

var debugHeader = "[" + this.process.id + "] - SendOSUToCRM - ";

var order = this.process.processOrder;
var childProcessDocument = this.activityData;

childProcessDocument.sourceInterfaceIndex = stcw.getInterfaceFolderCount (order, "GR_STATUS_UPD") - 1;
debugPrintln(stcc.getSysdateForLog() + debugHeader + "childProcessDocument.sourceInterfaceIndex " + childProcessDocument.sourceInterfaceIndex);

      if (stcw.createInterfaceFolder (order, "CRM_ORDER_STATUS_UPDATE") != null)
            childProcessDocument.targetInterfaceIndex = stcw.getInterfaceFolderCount (order, "CRM_ORDER_STATUS_UPDATE") - 1;
      else
            childProcessDocument.targetInterfaceIndex = -1;

debugPrintln(stcc.getSysdateForLog() + debugHeader + "childProcessDocument.targetInterfaceIndex " + childProcessDocument.targetInterfaceIndex);

      order.save ();

var debugHeader = "[" + this.process.id + "] - ChangeOrderStatus - ";

var order = this.process.processOrder;
var sourceInterfaceIndex = stcw.getInterfaceFolderCount (order, "GR_STATUS_UPD") - 1;

debugPrintln(stcc.getSysdateForLog() + debugHeader + "sourceInterfaceIndex " + sourceInterfaceIndex);

      var orderVersion = getOrderVersion_ (order);
      if (orderVersion == null)
            return;

      var graniteUpdate = findGraniteUpdate_ (orderVersion, sourceInterfaceIndex);
      if (graniteUpdate == null)
            return;

debugPrintln(stcc.getSysdateForLog() + debugHeader + "Update OrderStatus To " + graniteUpdate.workOrderStatus);

      order.header.orderStatus = graniteUpdate.workOrderStatus;
      order.save();


      //
      //  getOrderVersion_
      //
      function getOrderVersion_ (order) {

            var orderVersion = order.versions [order.versions.count - 1];
            if (orderVersion == null)
                  Global.logDebug ("could not get the order version");

            return (orderVersion);

      }            // g e t O r d e r V e r s i o n _

      //
      //  findGraniteUpdate_
      //
      function findGraniteUpdate_ (orderVersion, sourceInterfaceIndex) {

            var interfaceFolder = orderVersion.interfaces [sourceInterfaceIndex];
            if (interfaceFolder == null) {
                  Global.logDebug ("could not get the interfaceFolder");
                  return (null);
            }

            return (stcw.readStructure (interfaceFolder.interfaceData.responseData, "wgws:updateOrderStatus"));

      }            // f i n d G r a n i t e U p d a t e _

var order = this.process.processOrder;
if (order.header.orderStatus == "COMPLETED" || order.header.orderStatus == "CANCELLED")
      return (false);

return (true);
