method.script:
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - receivedWOSU = " + this.activityData.toXML());

var receivedWOSUNotification = this.activityData;
this.process.processDocument.latestWOSUId = this.activityData.wosuId;

// reading WOSU from DB
var receivedWOSU = stcw.readGraniteWOSUFromDB(this.activityData.wosuId);

// update status of the element
this.process.processOrderItem.lineItemStatus = receivedWOSU.workOrderStatus;

if((this.process.processOrder.orderHeader.orderType == "F") &&
   (this.process.processOrder.orderHeader.feasibilityFor.toUpperCase() == "CHANGE") &&
   (this.process.processOrderItem.provisioningBU == "W")) {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Not updating 'serviceNumber' because 'F' for 'CHANGE' for 'W'");
    // do nothing
}
else {
    this.process.processOrderItem.serviceNumber = (this.process.processOrderItem.isVAS ?
                                                       (this.process.processOrderItem.serviceNumber == null ? receivedWOSU.assetNumber : this.process.processOrderItem.serviceNumber) :
                                                       (receivedWOSU.assetNumber != null ? receivedWOSU.assetNumber : this.process.processOrderItem.serviceNumber));
}

/***
 *
 *  commented to avoid overwriting of the serviceNumber in case of VAS
 *
this.process.processOrderItem.serviceNumber = (receivedWOSU.assetNumber != null ? receivedWOSU.assetNumber : this.process.processOrderItem.serviceNumber);
**/

if(stcw.isLineItemCompleted(this.process.processOrderItem.lineItemStatus)) {
    if(this.process.processOrderItem.isCancel) {
        this.process.processOrderItem.lineItemStatus = "CANCELLED";
        this.process.processOrderItem.completionDate = new Date();
    }
    else {
        if(this.process.processOrder.orderHeader.orderType == "F") {
            if(receivedWOSU.feasibilityStatus != null) {
                this.process.processOrderItem.lineItemStatus = receivedWOSU.feasibilityStatus;

                if(stcw.checkIfLineItemIsFeasibleForQuote(this.process.processOrderItem.lineItemStatus.toUpperCase(), "F")) {
                    this.process.processOrderItem.completionDate = new Date();

                }
                else {
                    this.process.processOrderItem.lineItemStatus = receivedWOSU.feasibilityStatus;
                }
            }
            else {
                // not feasible status; assuming "Not Feasible"
                this.process.processOrderItem.lineItemStatus = "Not Feasible";
            }
            this.process.processOrderItem.reservationDays = receivedWOSU.reservationDays;
            this.process.processOrderItem.reservationNumber = receivedWOSU.reservationNumber;
        }
        else {
            this.process.processOrderItem.completionDate = new Date();
        }
    }
}

// 2015-12-31: adding update of CLLI according to WOSU data
if(receivedWOSU.nvPairs != null && receivedWOSU.nvPairs.length > 0) {
    for(var i=0; i<receivedWOSU.nvPairs.length; i++) {
        var paramName = receivedWOSU.nvPairs[i].name;
        var paramValue = receivedWOSU.nvPairs[i].value;

        if(paramName == "Cct-Site-A" && paramValue != null) {
            this.process.processOrderItem.locationACCLICode = paramValue;
        }
        if(paramName == "LOCATIONA" && paramValue != null) {
            this.process.processOrderItem.locationACCLICode = paramValue;
        }

        if(paramName == "Cct-Site-Z" && paramValue != null) {
            this.process.processOrderItem.locationBCCLICode = paramValue;
        }
        if(paramName == "LOCATIONZ" && paramValue != null) {
            this.process.processOrderItem.locationBCCLICode = paramValue;
        }
    }
}

this.process.processOrderItem.remarks = receivedWOSU.workOrderRemarks;
this.process.processOrderItem.save();

if(this.process.processOrder.orderHeader.orderType == "F") {
    stcw.updateBundleAndOrderHeaderStatusForFeasibility(this.process.processOrder, this.process.processOrderItem);
}
else {
    stcw.updateBundleAndOrderHeaderStatus(this.process.processOrder, this.process.processOrderItem);
}

// loading order and orderItem
var bundleOrder = this.process.processOrder;
var orderItem = this.process.processOrderItem;

var parentServiceType = (this.process.processDocument.parentServiceDocId == null ? orderItem.receivedServiceType : this.process.processDocument.parentServiceType);
var childServiceId = null;

// reading WOSU from DB
var receivedWOSU = stcw.readGraniteWOSUFromDB(this.process.processDocument.latestWOSUId);
if(receivedWOSU.workOrderStatus == "COMPLETED" || receivedWOSU.workOrderStatus == "CANCELLED") {
    childServiceId = stcw.getNextDependentSiblingChildService(parentServiceType, bundleOrder, orderItem.elementTypeInOrderTree, orderItem.priority, false, null);
}
else {
    if(receivedWOSU.taskStatusCode == 7 || receivedWOSU.taskStatusCode == 9 ||
       receivedWOSU.taskStatusCode == "COMPLETED" || receivedWOSU.taskStatusCode == "CANCELLED") {
        childServiceId = stcw.getNextDependentSiblingChildService(parentServiceType, bundleOrder, orderItem.elementTypeInOrderTree, orderItem.priority, true, receivedWOSU.taskOperation);
    }
    else {
        // WOSU not completed and taskStatusCode not in (7, 9) so ignored
    }
}

if(childServiceId != null) {
    var orchestrationRecord = stcw.getRecordInOrchestrationTableByLineItemId(childServiceId);
    if(orchestrationRecord != null && orchestrationRecord.provisionable) {
        if(orchestrationRecord.provisioningProcessId != null) {
            Process.sendSignal("stcw.notifyParentServiceSubmitted", orchestrationRecord.provisioningProcessId);
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Sending signal 'notifyParentServiceSubmitted' to process '" + orchestrationRecord.provisioningProcessId + "' for service '" + childServiceId + "'");
        }
        else {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Not able to send signal 'notifyParentServiceSubmitted' for service '" + childServiceId + "' because provisioningProcessId is null!");
        }
    }
}
else {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Not Sending signal 'notifyParentServiceSubmitted' because no child services found");
}

// checking lineItemStatus
var isLineItemCompleted = stcw.isLineItemCompleted(this.process.processOrderItem.lineItemStatus);

if(! isLineItemCompleted) {
    if(stcw.checkIfLineItemIsNotFeasibleForQuote(this.process.processOrderItem.lineItemStatus.toUpperCase(), this.process.processOrder.orderHeader.orderType) ||
       stcw.checkIfLineItemIsFeasibleForQuote(this.process.processOrderItem.lineItemStatus.toUpperCase(), this.process.processOrder.orderHeader.orderType)) {
        isLineItemCompleted = true;
    }
    else {
        // checking dummy WOSU
        var supportDummyWOSU = Global.getConfigVariable("DUMMY_WOSU_FOR_FAILED_ORDER", "FALSE");
        if(supportDummyWOSU == "TRUE") {
            // to know if WD is managing dummy WOSU, it checks if the status = 'FAILED' and also if
            // the lineItem has been accepted or not by Granite: if a dummy WOSU, the provisioning process is KO
            isLineItemCompleted = (this.process.processOrderItem.lineItemStatus == "FAILED" &&
                                   stcw.checkIfExistProcessKOInOrchestrationTblForLineItem(this.process.processOrder.id, this.process.processOrderItem.id));
        }
    }
}

return (! isLineItemCompleted);
