method.script:
var header = "[" + this.process.id + "] - terminateSIUProcessesThatExceededTimeout";


var processFinder = Finder.runFinder("stcw.findLongRunningProcessesForSIU", "select",  null);
if (processFinder != null && processFinder.length > 0) {
debugPrintln(stcc.getSysdateForLog() + header + "; found " + processFinder.length + " processes to be managed");
    for(var i=0; i<processFinder.length; i++) {
        var processDocument = Process.getProcessDocument(processFinder[i].processId);
debugPrintln(stcc.getSysdateForLog() + header + "; PID: " + processFinder[i].processId + "; ProcDoc: " + (processDocument != null ? processDocument.toXML() : "NULL"));

        if(processDocument != null) {
            var operationName = processDocument.interOperationName;
            if(operationName == "wsws:ifExpedtier_WHOLESALE/operation_SiteInformationUpdateAck") {
                Process.sendExceptionToProcess(processFinder[i].processId, "stcw.interruptProcessException");
debugPrintln(stcc.getSysdateForLog() + header + "; PID: " + processFinder[i].processId + "; sent Exception 'stcw.interruptProcessException' to process!");
            }
        }
    }
}
else {
debugPrintln(stcc.getSysdateForLog() + header + "; no SIU processes to be managed found");
}
