method.script:
// reset error flag
this.process.processDocument.errorType = null;

// loading order and orderItem
var bundleOrder = this.process.processOrder;
var orderItem = this.process.processOrderItem;


try {
    var inputData = null;
    var interfaceName = null;
    var operationName = null;

    if(this.process.processDocument.isWholesale) {
        inputData = stcw.mapOrderItemToWBUInputData_ForOrder(bundleOrder, orderItem.parentOrderItem, orderItem.elementTypeInOrderTree, true);
        interfaceName = "grws.XngOrderService";
        operationName = "submitOrder";
    }
    else {
        inputData = stcw.mapOrderItemToEBUInputData(bundleOrder, orderItem.parentOrderItem, orderItem.elementTypeInOrderTree, true);
        interfaceName = "cimeWS.XngOrderService";
        operationName = "submitOrder";
    }

debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - inputData = " + inputData.toXML());

    // stcc.sendDataToInterfaceWithLogging(inputData, interfaceName, operationName, existResult, processId, operationDescription, logUserData1, logUserData2)
    var result = stcc.sendDataToInterfaceWithLogging(inputData,
                                                     interfaceName,
                                                     operationName,
                                                     true,
                                                     this.process.id,
                                                     "SendSubmitOrderToGranite",
                                                     bundleOrder.orderHeader.orderNumber,
                                                     orderItem.workOrderNumber);

    stcw.writeGraniteResponseIntoDB(this.process.id, result);
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - sentDataToGranite; result " + (result == null ? "NULL" : result.toXML()));
}
catch(exc) {
    if(exc.message.indexOf("XE0023") >= 0 || exc.message.indexOf("XE0002")) {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - I/F " + interfaceName + "/" + operationName + " error: " + exc.message);
        this.process.processDocument.errorType = "Interface";
    }
    else {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Application Error while invoking " + interfaceName + "/" + operationName + "; error: " + exc.message);
        this.process.processDocument.errorType = "Application";
    }
}

var isInterfaceError = (this.process.processDocument.errorType == "Interface");
if(isInterfaceError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Interface error while sending submitOrder to Granite");
}

return isInterfaceError;

var isApplicationError = (this.process.processDocument.errorType == "Application");
if(isApplicationError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Application error while sending submitOrder to Granite");
}

return isApplicationError;

var isWholesale = this.process.processDocument.isWholesale;

// read orderAck in DB
var responseMDType = isWholesale ? "grws.submitOrderResponse" : "cimeWS.submitOrderResponse_el";
var graniteResult = stcw.readGraniteResponseFromDB(this.process.id, responseMDType);
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - receivedSubmitResponse = " + graniteResult.toXML());

var orderAcceptedByGranite = (isWholesale ? (graniteResult.result.Status.Status == "SUCCESS") :
                                            (graniteResult.return.status == "Accepted"));
//                                            (graniteResult.return.failure == null || graniteResult.return.failure.length == 0));

// updates info in lineItem
if(isWholesale) {
    this.process.processOrderItem.serviceNumber = graniteResult.result.AssetNumber;
}
this.process.processOrderItem.lineItemStatus = ((orderAcceptedByGranite) ? "READY" : "FAILED");
this.process.processOrderItem.alreadySentToGranite = orderAcceptedByGranite;
this.process.processOrderItem.sentAnytimeToGranite = (orderAcceptedByGranite || this.process.processOrderItem.sentAnytimeToGranite);
this.process.processOrderItem.save();

// updates bundle and orderHeader status
stcw.updateBundleAndOrderHeaderStatus(this.process.processOrder, this.process.processOrderItem);

// update orchestrationRecord to know if Granite accepted the WorkOrder
var recordInOrchestrationTable = stcw.getRecordInOrchestrationTableByLineItemId(this.process.processOrderItem.id);
if(orderAcceptedByGranite) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - submitOrderForElement = " + this.process.processOrderItem.id + " processed SUCCESSFULLY");
    this.process.processDocument.acceptedByGranite = true;
    // saving result of provisioning process
    recordInOrchestrationTable.provisioningProcessResult = "OK";
}
else {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - submitOrderForElement = " + this.process.processOrderItem.id + " REJECTED BY GRANITE!!!!");
    this.process.processDocument.acceptedByGranite = false;
    // saving result of provisioning process; KO ==> block all provisioning processes for such order
    recordInOrchestrationTable.provisioningProcessResult = "KO";
}
recordInOrchestrationTable.save();

if(!orderAcceptedByGranite) {
    // 2015-09-07: dummy WOSU management for failed order
    var supportDummyWOSU = Global.getConfigVariable("DUMMY_WOSU_FOR_FAILED_ORDER", "FALSE");
    if(supportDummyWOSU == "TRUE") {
        stcw.generateDummyWOSUForFailedOrderToGranite(this.process.processOrder.orderHeader.orderNumber, this.process.processOrderItem, "Wholesale", this.process.id);
    }
}

var orderItem = this.process.processOrderItem;
var workOrderType = null;

if(orderItem.workOrderNumber == null) {
    workOrderType = stcw.getWorkOrderType(this.process.processOrder.orderHeader.orderType, orderItem.action);
    if(workOrderType == null) {
        Global.throwException("Error in generating workOrderType");
    }
    orderItem.workOrderType = workOrderType;

    var workOrderNumber = stcw.generateWONumber(orderItem, workOrderType, this.process.processOrder);
    if(workOrderNumber == null) {
        Global.throwException("Error in generating workOrderNumber");
    }
    orderItem.workOrderNumber = workOrderNumber;
    orderItem.save();
}

var isSubmit = (this.process.processDocument.isSubmit);
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - isSubmit = " + isSubmit);

return isSubmit;

// reset error flag
this.process.processDocument.errorType = null;

// loading order and orderItem
var bundleOrder = this.process.processOrder;
var orderItem = this.process.processOrderItem;


try {
    var inputData = null;
    var interfaceName = null;
    var operationName = null;

    if(this.process.processDocument.isWholesale) {
        inputData = stcw.mapOrderItemToWBUInputData_ForOrder(bundleOrder, orderItem.parentOrderItem, orderItem.elementTypeInOrderTree, false);
        interfaceName = "grws.XngOrderService";
        operationName = "updateOrder";
    }
    else {
        inputData = stcw.mapOrderItemToEBUInputData(bundleOrder, orderItem.parentOrderItem, orderItem.elementTypeInOrderTree, false);
        interfaceName = "cimeWS.XngOrderService";
        operationName = "updateOrder";
    }
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - inputData = " + inputData.toXML());

    // stcc.sendDataToInterfaceWithLogging(inputData, interfaceName, operationName, existResult, processId, operationDescription, logUserData1, logUserData2)
    var result = stcc.sendDataToInterfaceWithLogging(inputData,
                                                     interfaceName,
                                                     operationName,
                                                     true,
                                                     this.process.id,
                                                     "sendUpdateOrderToGranite",
                                                     bundleOrder.orderHeader.orderNumber,
                                                     orderItem.workOrderNumber);

    stcw.writeGraniteResponseIntoDB(this.process.id, result);
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - sentDataToGranite; result " + (result == null ? "NULL" : result.toXML()));

}
catch(exc) {
    if(exc.message.indexOf("XE0023") >= 0 || exc.message.indexOf("XE0002")) {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - I/F " + interfaceName + "/" + operationName + " error: " + exc.message);
        this.process.processDocument.errorType = "Interface";
    }
    else {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Application Error while invoking " + interfaceName + "/" + operationName + "; error: " + exc.message);
        this.process.processDocument.errorType = "Application";
    }
}

var isInterfaceError = (this.process.processDocument.errorType == "Interface");
if(isInterfaceError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Interface error while sending updateOrder to Granite");
}

return isInterfaceError;

var isApplicationError = (this.process.processDocument.errorType == "Application");
if(isApplicationError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Application error while sending updateOrder to Granite");
}

return isApplicationError;

var isWholesale = this.process.processDocument.isWholesale;

// read orderAck in DB
var responseMDType = isWholesale ? "grws.updateOrderResponse" : "cimeWS.updateOrderResponse_el";
var graniteResult = stcw.readGraniteResponseFromDB(this.process.id, responseMDType);
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - receivedUpdateResponse = " + graniteResult.toXML());

var orderAcceptedByGranite = (isWholesale ? (graniteResult.result.Status.Status == "SUCCESS") :
                                            (graniteResult.return.status == "Accepted"));
//                                            (graniteResult.return.failure == null || graniteResult.return.failure.length == 0));

// updates info in lineItem
if(isWholesale) {
    if(this.process.processOrderItem.serviceNumber != graniteResult.result.AssetNumber) {
        this.process.processOrderItem.oldServiceNumber = this.process.processOrderItem.serviceNumber;
        this.process.processOrderItem.serviceNumber = graniteResult.result.AssetNumber;
    }
}
this.process.processOrderItem.lineItemStatus = ((orderAcceptedByGranite) ? "READY" : "FAILED");
this.process.processOrderItem.alreadySentToGranite = orderAcceptedByGranite;
this.process.processOrderItem.sentAnytimeToGranite = (orderAcceptedByGranite || this.process.processOrderItem.sentAnytimeToGranite);
this.process.processOrderItem.save();

// updates bundle and orderHeader status
stcw.updateBundleAndOrderHeaderStatus(this.process.processOrder, this.process.processOrderItem);

// update orchestrationRecord to know if Granite accepted the WorkOrder
var recordInOrchestrationTable = stcw.getRecordInOrchestrationTableByLineItemId(this.process.processOrderItem.id);
if(orderAcceptedByGranite) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - updateOrderForElement = " + this.process.processOrderItem.id + " processed SUCCESSFULLY");
    this.process.processDocument.acceptedByGranite = true;
    // saving result of provisioning process
    recordInOrchestrationTable.provisioningProcessResult = "OK";
}
else {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - updateOrderForElement = " + this.process.processOrderItem.id + " REJECTED BY GRANITE!!!!");
    this.process.processDocument.acceptedByGranite = false;
    // saving result of provisioning process; KO ==> block all provisioning processes for such order
    recordInOrchestrationTable.provisioningProcessResult = "KO";
}
recordInOrchestrationTable.save();

if(!orderAcceptedByGranite) {
    // 2015-09-07: dummy WOSU management for failed order
    var supportDummyWOSU = Global.getConfigVariable("DUMMY_WOSU_FOR_FAILED_ORDER", "FALSE");
    if(supportDummyWOSU == "TRUE") {
        stcw.generateDummyWOSUForFailedOrderToGranite(this.process.processOrder.orderHeader.orderNumber, this.process.processOrderItem, "Wholesale", this.process.id);
    }
}

var result = this.process.processDocument.acceptedByGranite;

var supportDummyWOSU = Global.getConfigVariable("DUMMY_WOSU_FOR_FAILED_ORDER", "FALSE");
if(supportDummyWOSU == "TRUE") {
    // if support dummy WOSU for Failed Orders, it has to continue
    result = true;
}
return result;

var orchestrationRecord = stcw.getRecordInOrchestrationTableByLineItemId(this.process.processOrderItem.id);

debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - sending signal 'notifyProvisioningIsCompleted' to orchestration process " + orchestrationRecord.orchestrationProcessId);


Process.sendSignal("stcw.notifyProvisioningIsCompleted", orchestrationRecord.orchestrationProcessId);

this.process.processDocument.firstPassToGranite = "Y";

// reset error flag
this.process.processDocument.errorType = null;

this.process.processDocument.isSubmit = this.process.processOrderItem.isSubmit;
this.process.processDocument.isQuote = false;
this.process.processDocument.isWholesale = (this.process.processOrderItem.provisioningBU == "W");
this.process.processDocument.isHome = false;

debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Start for element: " + this.process.processOrderItem.id);
