debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - sentDataToGranite; result " + (result == null ? "NULL" : result.toXML()));
}
catch(exc) {
    if(exc.message.indexOf("XE0023") >= 0 || exc.message.indexOf("XE0002")) {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - I/F grws.XngQuoteService/validateQuote error: " + exc.message);
        this.process.processDocument.errorType = "Interface";
    }
    else {
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Application Error while invoking grws.XngQuoteService/validateQuote; error: " + exc.message);
        this.process.processDocument.errorType = "Application";
    }
}

var isInterfaceError = (this.process.processDocument.errorType == "Interface");
if(isInterfaceError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Interface error while sending validateQuote to Granite");
}

return isInterfaceError;

var isApplicationError = (this.process.processDocument.errorType == "Application");
if(isApplicationError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Application error while sending validateQuote to Granite");
}

return isApplicationError;

// store orderAck in DB
var graniteResult = stcw.readGraniteResponseFromDB(this.process.id, "grws.validateQuoteResponse");

var orderAcceptedByGranite = (graniteResult.result.Status.Status == "SUCCESS");

// updates info in lineItem
this.process.processOrderItem.lineItemStatus = ((orderAcceptedByGranite) ? "READY" : "FAILED");
