    if(this.process.processOrder.orderHeader.feasibilityFor.toUpperCase() == "CHANGE") {
        quoteOrderType = "C";
    }
    workOrderType = stcw.getWorkOrderType(quoteOrderType, orderItem.action);
    if(workOrderType == null) {
