method.script:
// configured time for sleeping
var submitSleep = Global.getConfigVariable("SUBMITSLEEP", "10");

// if downtime
var ifDelay = Global.getInterfaceDelay("comOrdAck.orderAcknowledgement_PortType");
ifDelay = ((ifDelay != null) ? ifDelay : "10") ;

// sleepTime of the process = max(submitSleep, ifDelay);
var sleepTime = ((submitSleep > ifDelay) ? submitSleep : ifDelay);

debugPrintln(stcc.getSysdateForLog() + "[" + this.id + "] - sleep for : " + sleepTime + " secs -- {" + submitSleep + ";" + ifDelay + "}");

return sleepTime;

this.process.processDocument.errorType = null;

try {

    var graniteResponse = stcw.readGraniteResponseFromDB(this.process.processDocument.orderAckProcessId, "cimhJMS.orderAcknowledgementWithFailure_el");
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - submit/updateOrder Granite response = '" + graniteResponse.toXML() + "'");

    // map graniteResponse into data sent to CRM
    var request = new DataStructure("comOrdAck.receiveOrderAcknowledgement_el");
    stcw.fillHeaderForOrderACK_WOSU(request.orderAcknowledgementRequest.header, "OrderAck");

    var orderAcceptedByGranite = (graniteResponse.failure.status == "SUCCESS");

    var serviceLineItem = Document.readDoc("stcw.lineItemDocument", this.process.processDocument.lineItemId);

    request.orderAcknowledgementRequest.body.masterOrderNumber = this.process.processOrder.orderHeader.orderNumber;
    request.orderAcknowledgementRequest.body.serviceOrderNumber = serviceLineItem.workOrderNumber;
    request.orderAcknowledgementRequest.body.serviceOrderStatus = (orderAcceptedByGranite ? "SUBMITTED" : "FAILED");
    request.orderAcknowledgementRequest.body.masterOrderStatus = this.process.processOrder.orderHeader.orderStatus;
    request.orderAcknowledgementRequest.body.lineItemIdentifier = serviceLineItem.lineItemIdentifier;
    request.orderAcknowledgementRequest.body.serviceNumber = serviceLineItem.serviceNumber;

    if(!orderAcceptedByGranite) {
        var failure = new DataStructure("comOrdAck.failure");
        failure.errorCode = graniteResponse.failure.errorCode;
        failure.errorDescription = graniteResponse.failure.errorDescription;
        failure.errorTime = graniteResponse.failure.errorTime;
        failure.errorType = graniteResponse.failure.errorType;
        failure.functionName = graniteResponse.failure.functionName
        failure.objectId = serviceLineItem.lineItemIdentifier;
        failure.processInstanceId = null;
        failure.sourceCodeError = graniteResponse.failure.sourceErrorCode;
        failure.systemName = graniteResponse.failure.systemName;
        failure.targetRecord = null;
        failure.userId = null;
        request.orderAcknowledgementRequest.body.failure[0] = failure;
    }

debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - orderAck to CRM = '" + request.toXML() + "'");

    // sending data to EAI
    var response = stcc.sendDataToInterfaceWithLogging(request,
                                                       "comOrdAck.orderAcknowledgement_PortType",
                                                       "receiveOrderAcknowledgement",
                                                       true,
                                                       this.process.id,
                                                       "SendOrderAckToCOM",
                                                       this.process.processOrder.orderHeader.orderNumber,
                                                       serviceLineItem.workOrderNumber);
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - COM orderAck response = '" + response.toXML() + "'");
}
catch(exc) {
    if(exc.message.indexOf("XE0023") >= 0 || exc.message.indexOf("XE0002")) {
        debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - I/F comOrdAck.orderAcknowledgement_PortType/receiveOrderAcknowledgement error: " + exc.message);
        this.process.processDocument.errorType = "Interface";
    }
    else {
        this.process.processDocument.errorType = "Application";
    }
}

var isInterfaceError = (this.process.processDocument.errorType == "Interface");
if(isInterfaceError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Interface error while sending orderAck to EAI");
}

return isInterfaceError;

var isApplicationError = (this.process.processDocument.errorType == "Application");
if(isApplicationError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Application error while sending orderAck to EAI");
}

return isApplicationError;
