method.script:
var debugHeader = "[" + this.process.id + "] - UpdateOrderStatus - ";

var order = this.process.processOrder;
var processDocument = this.process.processDocument;
processDocument.exceptionType = null;

try {
      var orderVersion = getOrderVersion_ (order);
      if (orderVersion == null)
            return;

      var graniteUpdate = findGraniteUpdate_ (orderVersion, processDocument);
      if (graniteUpdate == null)
            return;

      var request = new DataStructure("siosu:processRequest_el");

debugPrintln(stcc.getSysdateForLog() + debugHeader + "graniteUpdate = '" + graniteUpdate.toXML() + "'");

            stcw.map_updateOrderStatus_orderUpdate (request, graniteUpdate);

            stcw.ensureInterfaceFolder (order, "CRM_ORDER_STATUS_UPDATE");
            order.save ();

            // check payload size
            stcc.checkPayloadToCRMSize(request, this.process.processDocument.interOperationName);

            // sending data to EAI
            var response = stcw.sendOrderUpdateToEAI(request, this.process.id);

            rpts.updateHistoryEvent (this);

            var interfaceFolder = stcw.storeInterfaceMessage (getInterfaceFolder_ (order, processDocument), response);
            if (interfaceFolder != null)
                  process.processOrderItemId = interfaceFolder.interfaceData.id;

            if (response == null)
                  processDocument.exceptionType = "Interface";

            order.save ();
      }
      catch(exc) {
debugPrintln(stcc.getSysdateForLog() + debugHeader + "Catched Exception " + exc.message);
            processDocument.exceptionType = "Application";
      }




      //
      //  getOrderVersion_
      //
      function getOrderVersion_ (order) {

            var orderVersion = order.versions [order.versions.count - 1];
            if (orderVersion == undefined || orderVersion == null)
                  Global.logDebug ("could not get the order version");

            return (orderVersion);

      }            // g e t O r d e r V e r s i o n _

      //
      //  findGraniteUpdate_
      //
      function findGraniteUpdate_ (orderVersion, processDocument) {
debugPrintln(stcc.getSysdateForLog() + debugHeader + "processDocument.sourceInterfaceIndex " + processDocument.sourceInterfaceIndex);

            var interfaceFolder = orderVersion.interfaces [processDocument.sourceInterfaceIndex];
            if (interfaceFolder == null) {
                  Global.logDebug ("could not get the interfaceFolder");
                  return (null);
            }

            return (stcw.readStructure (interfaceFolder.interfaceData.responseData, "wgws:updateOrderStatus"));

      }            // f i n d G r a n i t e U p d a t e _


      //
      //  getInterfaceFolder_
      //
      function getInterfaceFolder_ (order, processDocument) {

            var versionNumber = order.versions.count - 1;
            if (versionNumber < 0)
                  return (null);

            return (order.versions [versionNumber].interfaces [processDocument.targetInterfaceIndex]);

      }            // g e t I n t e r f a c e F o l d e r _
