method.script:
// configured time for sleeping
var submitSleep = Global.getConfigVariable("SUBMITSLEEP", "10");

// if downtime
var ifDelay = Global.getInterfaceDelay("comOrderStatusUpdate:orderStatusUpdate_PortType");
ifDelay = ((ifDelay != null) ? ifDelay : "10") ;

// sleepTime of the process = max(submitSleep, ifDelay);
var sleepTime = ((submitSleep > ifDelay) ? submitSleep : ifDelay);

debugPrintln(stcc.getSysdateForLog() + "[" + this.id + "] - sleep for : " + sleepTime + " secs -- {" + submitSleep + ";" + ifDelay + "}");

return sleepTime;

this.process.processDocument.errorType = null;

try {
    var graniteWOSU = stcw.readGraniteWOSUFromDB(this.process.processDocument.latestWOSUId);

debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - WOSU = '" + graniteWOSU.toXML() + "'");

    var existOrder = (this.process.processOrder != null);

    var woStatus = graniteWOSU.workOrderStatus;
    if(existOrder && woStatus == "COMPLETED") {
        if(this.process.processOrderItem.isCancel) {
            woStatus = "CANCELLED";
        }
    }

    // map graniteWOSU into data sent to COM
    var request = new DataStructure("comOrderStatusUpdate.receiveOrderStatusUpdate_el");
    stcw.fillHeaderForOrderACK_WOSU(request.orderStatusUpdateRequest.header, "WorkOrderStatusUpdate");

    // STCSUP-102 - in case the WO is completed and the serviceStatus = "READY TO ACT", set the serviceStatus to "LIVE"
    var receivedServiceStatus = graniteWOSU.lineItemStatus;
    if(graniteWOSU.workOrderStatus == "COMPLETED" && graniteWOSU.lineItemStatus == "READY TO ACT") {
        receivedServiceStatus = "LIVE";
    }

    request.orderStatusUpdateRequest.body.orderNo = graniteWOSU.comOrderNumber;
    request.orderStatusUpdateRequest.body.orderStatus = (existOrder ? this.process.processOrder.orderHeader.orderStatus : graniteWOSU.workOrderStatus);
    request.orderStatusUpdateRequest.body.businessUnit = (existOrder ? this.process.processOrder.orderHeader.businessUnit : graniteWOSU.businessUnit);
    request.orderStatusUpdateRequest.body.reservationNo = graniteWOSU.reservationNumber;
    request.orderStatusUpdateRequest.body.feasibilityStatus = (graniteWOSU.feasibilityStatus != null ? graniteWOSU.feasibilityStatus : receivedServiceStatus);
    request.orderStatusUpdateRequest.body.reservationDays = graniteWOSU.reservationDays;
    request.orderStatusUpdateRequest.body.actualImplementationDays = graniteWOSU.actualImplementationDays;
    request.orderStatusUpdateRequest.body.plannedFeasibleDays  = graniteWOSU.plannedFeasibleDays;
    request.orderStatusUpdateRequest.body.lineItemIdentifier = (existOrder ? this.process.processOrderItem.lineItemIdentifier :
                                                                             (graniteWOSU.lineItemIdentifier != null ? graniteWOSU.lineItemIdentifier : -1));
    request.orderStatusUpdateRequest.body.lineItemStatus = receivedServiceStatus;
    request.orderStatusUpdateRequest.body.assetNo = (existOrder ? this.process.processOrderItem.serviceNumber : graniteWOSU.assetNumber);
    request.orderStatusUpdateRequest.body.niNo = graniteWOSU.niNumber;
    request.orderStatusUpdateRequest.body.systemDesignation = graniteWOSU.systemDesignation;
    request.orderStatusUpdateRequest.body.workOrderName = (existOrder ? this.process.processOrderItem.workOrderNumber : graniteWOSU.workOrderName);
    request.orderStatusUpdateRequest.body.workOrderStatus = woStatus;
    request.orderStatusUpdateRequest.body.workOrderRemarks = graniteWOSU.workOrderRemarks;
    request.orderStatusUpdateRequest.body.workOrderProblemDescription = graniteWOSU.workOrderProblemDescription;
    request.orderStatusUpdateRequest.body.taskNumber = graniteWOSU.taskNumber;
    request.orderStatusUpdateRequest.body.taskName = graniteWOSU.taskName;
    request.orderStatusUpdateRequest.body.taskAllocatedTime = graniteWOSU.taskAllocatedTime;
    request.orderStatusUpdateRequest.body.taskDescription = graniteWOSU.taskDescription;
    request.orderStatusUpdateRequest.body.taskOperation = graniteWOSU.taskOperation;
    request.orderStatusUpdateRequest.body.taskPriority = graniteWOSU.taskPriority;
    // 2015-06-15: according to Rao email, renamed attribute in Granite JMS event from taskServiceStatus to serviceStatus
    // request.orderStatusUpdateRequest.body.taskServiceStatus = graniteWOSU.taskServiceStatus;
    request.orderStatusUpdateRequest.body.taskServiceStatus = receivedServiceStatus;
    request.orderStatusUpdateRequest.body.taskStatusCode = graniteWOSU.taskStatusCode;
    request.orderStatusUpdateRequest.body.taskRemarks = graniteWOSU.taskRemarks;
    request.orderStatusUpdateRequest.body.workOrderStage = graniteWOSU.workOrderStage;

    if(graniteWOSU.nvPairs != null && graniteWOSU.nvPairs.length > 0) {
        for(var i=0; i<graniteWOSU.nvPairs.length; i++) {
            var parameter = new DataStructure("comOrderStatusUpdate.attributes");
            parameter.name = graniteWOSU.nvPairs[i].name;
            parameter.value = graniteWOSU.nvPairs[i].value;
            request.orderStatusUpdateRequest.body.attributes[i] = parameter;
        }
    }

debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - WOSU to COM = '" + request.toXML() + "'");

    // sending data to EAI
    var response = stcc.sendDataToInterfaceWithLogging(request,
                                                       "comOrderStatusUpdate.orderStatusUpdate_PortType",
                                                       "receiveOrderStatusUpdate",
                                                       true,
                                                       this.process.id,
                                                       "SendWOSUToCOM",
                                                       (existOrder ? this.process.processOrder.orderHeader.orderNumber : graniteWOSU.comOrderNumber),
                                                       (existOrder ? this.process.processOrderItem.workOrderNumber : graniteWOSU.workOrderName));
debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - COM WOSU response = '" + response.toXML() + "'");
}
catch(exc) {
    if(exc.message.indexOf("XE0023") >= 0 || exc.message.indexOf("XE0002")) {
        debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - I/F comOrderStatusUpdate.orderStatusUpdate_PortType error: " + exc.message);
        this.process.processDocument.errorType = "Interface";
    }
    else {
        this.process.processDocument.errorType = "Application";
    }
}

var isInterfaceError = (this.process.processDocument.errorType == "Interface");
if(isInterfaceError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Interface error while sending WOSU to EAI");
}

return isInterfaceError;

var isApplicationError = (this.process.processDocument.errorType == "Application");
if(isApplicationError) {
    debugPrintln(stcc.getSysdateForLog() + "[" + this.process.id + "] - Application error while sending WOSU to EAI");
}

return isApplicationError;
