method.script:
var debugHeader = "[" + this.process.id + "] SendUpdateOrderToGranite - ";

var order = this.process.processOrder;
var processDocument = this.process.processDocument;
processDocument.exceptionType = null;

try {
      // extracting data must be sent to Granite
      var crmInput = findCrmInput_ (order);
      var request = new DataStructure("grws:updateOrder");

debugPrintln(stcc.getSysdateForLog() + debugHeader + "crmInput = '" + crmInput.toXML() + "'");

            stcw.map_updateOrder_updateOrder (request, crmInput, this.process.id);

            stcw.ensureInterfaceFolder (order, "GR_UPD_ORDER");
            order.save ();

            var response = null;

            // in case of UpdateOrder, the order is already in Granite so the process has to ignore the skip flag!!!!
            // if(order.header.skipSendToGranite == null || order.header.skipSendToGranite == "N") {
                  // sending data to Granite
                  response = stcw.sendUpdateOrderToGranite(request, this.process.id);

                  // managing response
                  rpts.updateHistoryEvent (this);
            /***
            }
            else {
                  // skipping send order to granite ... and generating fake response ...
                  var response = new DataStructure("grws:submitOrderResponse");
                  response.result.OrderNumber = order.header.orderNumber;
                  response.result.AssetNumber = order.header.orderNumber;
                  response.result.WorkOrderStatus = "New";
                  response.result.AssetStatus = "New";
                  response.result.BusinessUnit = "Wholesale";
                  response.result.Status.Status = "SUCCESS";
            }
            ***/

            var interfaceFolder = stcw.storeInterfaceMessage (stcw.findLastInterfaceFolder (order, "GR_UPD_ORDER"), response);
            if (interfaceFolder != null)
                  this.process.processOrderItemId = interfaceFolder.interfaceData.id;

            if (response == null) {
                  processDocument.exceptionType = "Interface";
            }
            else {
                  processDocument.ackResult = response.result.Status.Status;
            }

            order.save ();
      }
      catch(exc) {
debugPrintln(stcc.getSysdateForLog() + debugHeader + "Catched Exception " + exc.message);
            processDocument.exceptionType = "Application";
      }

      //
      //  findCrmInput_
      //
      function findCrmInput_ (order) {

            var crmInterfaceFolder = stcw.findLastInterfaceFolder (order, "CRM_ORD_REQUEST");
            if (crmInterfaceFolder == null) {
                  Global.logDebug ("failed to get crmInterfaceFolder [CRM_ORD_REQUEST]");
                  return (null);
            }

            return (stcw.readStructure (crmInterfaceFolder.interfaceData.responseData , "wsws:updateOrder"));

      }            // f i n d C r m I n p u t _
