script.script:
if(debugMsg != null) {
    debugPrintln(stcc.getSysdateForLog() + " " + debugMsg + ": Found error [" + errorCode + ";" + errorDescription + "]");
}


if(validationErrors == null) {
    validationErrors = new Array();
}

validationErrors.push(errorCode);
validationErrors.push(errorDescription);


return validationErrors;
