script.script:
var inputDoc = new Document("stcw.STCW_ORDER_ORCHESTRATION");
inputDoc.cwOrderId = cwOrderId;
inputDoc.cwDocId = lineItemId;
inputDoc.provisioningProcessResult = "KO";

var orchestrationRecordList = Finder.runFinder("stcw.findSTCW_ORDER_ORCHESTRATION", "select", inputDoc);

var existProcessKO = false;
if(orchestrationRecordList != null && orchestrationRecordList.length > 0) {
    existProcessKO = true;
}

debugPrintln(stcc.getSysdateForLog() + " existProcessKOInOrchestrationTable for OrderId " + cwOrderId + " & LineItemId " + lineItemId + " = " + existProcessKO);
return existProcessKO;
