script.script:
var inputDoc = new Document("stcw.STCW_ORDER_ORCHESTRATION");
inputDoc.cwOrderId = cwOrderId;

var orchestrationRecordList = Finder.runFinder("stcw.findSTCW_ORDER_ORCHESTRATION", "select", inputDoc);

var existProvisioningProcess = false;
if(orchestrationRecordList != null && orchestrationRecordList.length > 0) {
    for(var i=0; i<orchestrationRecordList.length && (!existProvisioningProcess); i++ ){
        existProvisioningProcess = (orchestrationRecordList[i].provisioningProcessId != null);
    }
}

debugPrintln("checkIfExistProvisionProcessInOrchestrationTable for Order " + cwOrderId + " = " + existProvisioningProcess);
return existProvisioningProcess;
