script.script:
var validationErrors = null;

if(existingOrder.orderHeader != null && receivedOrder.orderHeader != null) {

    // the function scans the configuration table STCW_SAME_ATTRIBUTES_IN_UPDATE to find all the attribues in "OrderHeader" ('OH') for Cancel or for Revise
    var attributesToCheck = stcw.getAttributesToCheckInUpdate('OH', isCancel);

    if(receivedOrder.orderHeader.orderType != null && receivedOrder.orderHeader.orderType == "F") {
        attributesToCheck.push("feasibilityFor");
        attributesToCheck.push("reservation");
    }

    for(var i=0; i<attributesToCheck.length; i++) {
        var attributeToCheck = attributesToCheck[i];
        compareValues_(receivedOrder.orderHeader[attributeToCheck], existingOrder.orderHeader[attributeToCheck], attributeToCheck, "Attribute");
    }

    // 2016-05-30: don't check NV Pairs for Cancel
    if(!isCancel) {
        // nv check; it loops in existing NV pair and verifies if it exists also in received one
        validationErrors = stcw.compareReceivedNV_OHParamsWithExistingOne(existingOrder, receivedOrder, validationErrors, existingOrder.orderHeader.orderNumber);
    }

    // completed check - checking if there are validation errors
    if(validationErrors != null) {
        failureArray = stcw.addValidationErrorsToFailureArrayForSub_UpdInWD(validationErrors, existingOrder.orderHeader.orderNumber, failureArray, false);
    }

}
else {
    validationErrors = ["AE0058", Global.translateText("AE0058", null, [(existingOrder.orderHeader != null ? "Received" : "Existing"), existingOrder.orderHeader.orderNumber])];
    failureArray = stcw.addValidationErrorsToFailureArrayForSub_UpdInWD(validationErrors, existingOrder.orderHeader.orderNumber, failureArray, false);
}

return failureArray;


function notIn_(attributeName, attributeNameToSkipArray) {
    var found = false;
    for (var i=0; i<attributeNameToSkipArray.length && !found; i++) {
        found = (attributeName == attributeNameToSkipArray[i]);
    }

    return (!found);
}


function compareValues_(receivedAttribute, existingAttribute, attributeName, category) {
    var debugMsg = "compareAndUpdateReceivedOrderHeaderWithExistingOne (" + existingOrder.orderHeader.orderNumber + ")";
    if(receivedAttribute == null) {
        if(existingAttribute != null) {
            validationErrors = stcw.appendToValidationErrors("AE0055", Global.translateText("AE0055", null, [category, attributeName, "existing"]), validationErrors, debugMsg);
        }
    }
    else {
        if(existingAttribute == null) {
            validationErrors = stcw.appendToValidationErrors("AE0057", Global.translateText("AE0057", null, [category, attributeName, "existing"]), validationErrors, debugMsg);
        }
        else {
            if(attributeName == 'creationDate' || attributeName == 'serviceDate') {
                var receivedDate = new Date(receivedAttribute.getFullYear(), receivedAttribute.getMonth(), receivedAttribute.getDate());
                var existingDate = new Date(existingAttribute.getFullYear(), existingAttribute.getMonth(), existingAttribute.getDate());

                if(receivedDate.getTime() != existingDate.getTime()) {
                    validationErrors = stcw.appendToValidationErrors("AE0056", Global.translateText("AE0056", null, [category, attributeName, receivedDate, existingDate, "existing"]), validationErrors, debugMsg);
                }
            }
            else {
                if(! receivedAttribute.toString().equals(existingAttribute.toString())) {
                    validationErrors = stcw.appendToValidationErrors("AE0056", Global.translateText("AE0056", null, [category, attributeName, receivedAttribute, existingAttribute, "existing"]), validationErrors, debugMsg);
                }
            }
        }
    }
}
