script.script:
var debugMsg = "compareReceivedBundleBlockParamsWithExistingOne (" + lineItemIdentifier + ")";

// nv check; it loops in existing NV pair and verifies if it exists also in received one
if(existingLineItemContainer.parentLineItemBlockParameters != null) {
    var missingReceived = 0;
    for(var i=0; i<existingLineItemContainer.parentLineItemBlockParameters.length; i++) {
        var existingNVName = existingLineItemContainer.parentLineItemBlockParameters[i].parentLineItemBlockParameter.name;
        var found = false;
        var foundWithError = false;
        var lastErrorFound = null;
        // search nv in received
        if(receivedLineItemContainer.parentLineItemBlockParameters != null) {
            for(var j=0; j<receivedLineItemContainer.parentLineItemBlockParameters.length && !found; j++) {
                if(existingNVName == receivedLineItemContainer.parentLineItemBlockParameters[j].parentLineItemBlockParameter.name) {
                    // found; comparing the values
                    found = true;
                    var receivedValues = stcw.getBlockValuesAsString(receivedLineItemContainer.parentLineItemBlockParameters[j].parentLineItemBlockParameter.id, ";");
                    var existingValues = stcw.getBlockValuesAsString(existingLineItemContainer.parentLineItemBlockParameters[i].parentLineItemBlockParameter.id, ";");
                    var resultArray = compareValues_(receivedValues, existingValues, existingNVName, "BlockParameter");
                    if(resultArray != null) {
                        found = false;
                        foundWithError = true;
                        lastErrorFound = resultArray;
                    }
                    else {
                        foundWithError = false;
                        lastErrorFound = null;
                    }
                }
            }
        }

        if(!found) {
            if(foundWithError) {
                // found but it was wrong! adding error
                validationErrors = stcw.appendToValidationErrors(lastErrorFound[0], lastErrorFound[1], validationErrors, debugMsg);
            }
            else {
                // not found NV; adding error
                validationErrors = stcw.appendToValidationErrors("AE0065", Global.translateText("AE0065", null, ["BlockParameter", existingNVName, "ParentLineItem", lineItemIdentifier, "received", "existing"]), validationErrors);
                missingReceived++;
            }
        }
    }

    if(receivedLineItemContainer.parentLineItemBlockParameters != null) {
        if((receivedLineItemContainer.parentLineItemBlockParameters.length + missingReceived) != existingLineItemContainer.parentLineItemBlockParameters.length) {
            // it means that there are additional attributes in received that are not in existing! ERROR!
            validationErrors = stcw.appendToValidationErrors("AE0066", Global.translateText("AE0066", null, ["ParentLineItem", lineItemIdentifier, "BlockParameters"]), validationErrors);
        }
    }
}
else {
    if(receivedLineItemContainer.parentLineItemBlockParameters != null) {
        // it means that there are additional attributes in received that are not in existing! ERROR!
        validationErrors = stcw.appendToValidationErrors("AE0066", Global.translateText("AE0066", null, ["ParentLineItem", lineItemIdentifier, "BlockParameters"]), validationErrors);
    }
}

return validationErrors;

function compareValues_(receivedAttribute, existingAttribute, attributeName, category) {
    var resultArray = null;

    if(receivedAttribute == null) {
        if(existingAttribute != null) {
            resultArray = ["AE0055", Global.translateText("AE0055", null, [category, attributeName, "existing"])];
        }
    }
    else {
        if(existingAttribute == null) {
            resultArray = ["AE0057", Global.translateText("AE0057", null, [category, attributeName, "existing"])];
        }
        else {
            if(! receivedAttribute.toString().equals(existingAttribute.toString())) {
                resultArray = ["AE0056", Global.translateText("AE0056", null, [category, attributeName, receivedAttribute, existingAttribute, "existing"])];
            }
        }
    }

    return resultArray;
}
