var result = null;

if(stcc.stringHasValue(comAttributeName)) {
    var convertValueFinder = new Finder ("stcconf.mapCOMToGraniteNameFinder");
    convertValueFinder.searchDocument.comAttributeName = comAttributeName.toUpperCase();
    convertValueFinder.search();

    var resultList = convertValueFinder.list;

    if (resultList != null && resultList.size > 0) {
        result = resultList.start.graniteAttributeName;
    }
    else {
        result = comAttributeName;
    }
}

return result;
