var result = null;

if(stcc.stringHasValue(comServiceType)) {
    var convertValueFinder = new Finder ("stcconf.mapCOMToGraniteServiceTypeFinder");
    convertValueFinder.searchDocument.serviceType = comServiceType;
    convertValueFinder.search();

    var resultList = convertValueFinder.list;

    if (resultList != null && resultList.size > 0) {
        result = resultList.start.giServiceType;
    }
}

return result;
