script.script:
if (order == null)
      return (null);

if (versionNumber == null)
      versionNumber = order.versions.count - 1;

if (versionNumber < 0) {
      Global.logDebug ("createInterfaceFolder (): versionNumber must be non-negative");
      return (null);
}

if (order.versions [versionNumber].interfaces == null)
      order.versions [versionNumber].addItem ("interfaces");

var interfaces = order.versions [versionNumber].interfaces;
var interfaceFolder = interfaces.newInstance ();
interfaceFolder.interfaceData.interfaceName = interfaceName;

return (interfaceFolder);
