if (order == null) {
      Global.logDebug ("findLastInterfaceFolder (): order is required");
      return (null);
}

if (interfaceName == null) {
      Global.logDebug ("findLastInterfaceFolder (): interfaceName is required");
      return (null);
}

if (versionNumber == null)
      versionNumber = order.versions.count - 1;

if (versionNumber < 0) {
      Global.logDebug ("findLastInterfaceFolder (): version number must be non-negative");
      return (null);
}

var orderVersion = order.versions [versionNumber];
if (orderVersion.interfaces == null)
      return (null);

for (var index = orderVersion.interfaces.length - 1; index >= 0; index--) {
      if (orderVersion.interfaces [index].interfaceData.interfaceName == interfaceName)
            return (orderVersion.interfaces [index]);
}

return (null);
