script.script:
var inputDoc = new Document("stcconf.STCW_ATTR_SAME_VAL_IN_UPDATE");
inputDoc.elementType = elementType;
inputDoc.isCancel = isCancel;

var resultList = Finder.runFinder("stcconf.findAttributesWithSameValueInUpdate", "select", inputDoc);

var attributeNames = null;

if(resultList != null && resultList.length > 0) {
    attributeNames = new Array();

    for(var i=0; i<resultList.length; i++) {
        attributeNames.push(resultList[i].attributeName);
    }
}

return attributeNames;
